$((function(){JSON.parse(localStorage.getItem("cart")).map(n=>{n.sale?$(`<div data-id=${n.id} class="item flex a-center j-between">\n          <img src=${n.image} alt="">\n          <div class="info">\n            <div class="name">\n              <a class = "show" href = "http://localhost:3000/viewproduct.html">\n              <span>${n.name}</span>\n              </a>\n            </div>\n            <div class="price-content">\n              <span class="quantity">${n.quantity} x</span>\n              <span class="price">$${Math.floor(n.price-n.price*n.sale/100)}.00</span>\n            </div>\n          </div>\n          <button id="delete">X</button>\n        </div>`).appendTo(".products-on-cart"):$(`<div data-id=${n.id} class="item flex a-center j-between">\n          <img src=${n.image} alt="">\n          <div class="info">\n            <div class="name">\n              <a class = "show" href = "http://localhost:3000/viewproduct.html">\n              <span>${n.name}</span>\n              </a>\n            </div>\n            <div class="price-content">\n              <span class="quantity">${n.quantity} x</span>\n              <span class="price">$${n.price}.00</span>\n            </div>\n          </div>\n          <button id="delete">X</button>\n        </div>`).appendTo(".products-on-cart")})}));